mapper.update:
UPDATE tblboard
SET title = #{title}, content = #{content}
WHERE bno = #{bno}

UPDATE tblboard
	SET viewcnt = viewcnt+1
	WHERE bno = #{bno}

UPDATE tblboard
	SET goodcnt = goodcnt+1
	WHERE bno = #{bno}

UPDATE tblboard
	SET downcnt = downcnt+1
	WHERE bno = #{bno}

mapper.select:
SELECT COUNT(*) 
FROM tblreply 
WHERE bno= #{bno}

SELECT *
FROM member
WHERE mid=#{mid} AND mpw=#{mpw}

SELECT filename
FROM tblboard
WHERE bno= #{bno}

mapper.update:
UPDATE tblboard
SET re_step = re_step + 1
WHERE ref = #{ref} AND re_step >= #{re_step}
